query GetMattertags($modelId: ID!, $includeDisabled: Boolean!) {
  model(id: $modelId) {
    id
    mattertags(includeDisabled: $includeDisabled) {
      ...MattertagDetails
      __typename
    }
    __typename
  }
}

fragment MattertagDetails on Mattertag {
  id
  label
  description
  color
  enabled
  floor {
    id
    meshId
    __typename
  }
  room {
    id
    meshId
    __typename
  }
  anchorPosition {
    x
    y
    z
    __typename
  }
  stemEnabled
  stemNormal {
    x
    y
    z
    __typename
  }
  stemLength
  media
  mediaType
  created
  modified
  __typename
}
