query GetSnapshots($modelId: ID!) {
  model(id: $modelId) {
    id
    assets {
      photos {
        ...PhotoDetails
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment PhotoDetails on Photo {
  id
  label
  classification
  category
  height
  width
  created
  modified
  status
  filename
  format
  url
  resolutions
  type
  origin
  validUntil
  thumbnailUrl: resizeUrl(resolution: thumbnail)
  presentationUrl: resizeUrl(resolution: presentation)
  snapshotLocation {
    viewMode
    position {
      x
      y
      z
      __typename
    }
    rotation {
      x
      y
      z
      w
      __typename
    }
    zoom
    floorVisibility {
      id
      meshId
      sequence
      __typename
    }
    anchor {
      id
      pano {
        id
        placement
        __typename
      }
      __typename
    }
    __typename
  }
  __typename
}
